import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Cart.css';

const Cart = () => {
  const [cart, setCart] = useState({ items: [] });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    const token = localStorage.getItem('token');
    if (token) {
      // Authenticated user: fetch from backend
      try {
        const res = await axios.get('http://localhost:5000/api/cart', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCart(res.data);
      } catch (err) {
        console.error(err);
      }
    } else {
      // Guest user: load from localStorage
      const guestCart = JSON.parse(localStorage.getItem('guestCart')) || { items: [] };
      setCart(guestCart);
    }
    setLoading(false);
  };

  const updateQuantity = async (itemId, quantity) => {
    const token = localStorage.getItem('token');
    try {
      await axios.put(`http://localhost:5000/api/cart/update/${itemId}`, 
        { quantity },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchCart();
    } catch (err) {
      console.error(err);
    }
  };

  const removeItem = async (itemId) => {
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:5000/api/cart/remove/${itemId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart();
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (!cart || cart.items.length === 0) return <div>Your cart is empty</div>;

  return (
    <div className="cart">
      <h2>Shopping Cart</h2>
      <div className="cart-items">
        {cart.items.map(item => (
          <div key={item._id} className="cart-item">
            <img src={item.product.image} alt={item.product.name} />
            <div>
              <h3>{item.product.name}</h3>
              <p>${item.product.price}</p>
              <input
                type="number"
                value={item.quantity}
                onChange={(e) => updateQuantity(item._id, parseInt(e.target.value))}
                min="1"
              />
              <button onClick={() => removeItem(item._id)}>Remove</button>
            </div>
          </div>
        ))}
      </div>
      <div className="cart-total">
        <p>Total: ${cart.items.reduce((total, item) => total + item.product.price * item.quantity, 0)}</p>
        <Link to="/checkout" className="btn-primary">Checkout</Link>
      </div>
    </div>
  );
};

export default Cart;
